mapper.select:
SELECT push_id
FROM push
WHERE ref_id = #{refId} AND receiver_id = #{receiverId}

SELECT cmt_count
FROM push
WHERE ref_id = #{refId} AND receiver_id = #{receiverId}

mapper.update:
UPDATE push
SET cmt_count = cmt_count + 1, push_date = SYSTIMESTAMP, read='F', deleted = 'F'
WHERE ref_id = #{refId}

UPDATE push
SET read = 'T'
WHERE receiver_id = #{userId}
